    var blockers []*model.Issue
    for _, dep := range m.labelDashboard.upstream[issueID] {
        if dep.Type == model.DepBlocks {
            if blocker := m.labelDashboard.issueMap[dep.DependsOnID]; blocker != nil {
                if blocker.Status.IsOpen() {
                    blockers = append(blockers, blocker)
                }
            }
        }
    }
    return blockers
}
